import {useState, useEffect} from 'react'
import axios from "axios"
import "bootstrap/dist/css/bootstrap.min.css"
import Data from "./Data"


export const CategoryContext = createContext()


export default function Categorie(){

    const [categories, setCategories] = useState([])

    const [selectedCategory, setSelectedCategory] = useState(null);

    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/categories/')
            setCategories(result.data)
        }

        fetchdata()
    }, [])


    const handleCategorySelection = (categorie) => {
        setSelectedCategory(categorie);
    }




    return(
    <>
    <CategoryContext.Provider value={{ selectedCategory, setSelectedCategory }}>
        <div>
       {categories.map(categorie=>{
           return(
               <li key={categorie.id} onClick={() => handleCategorySelection(categorie.nom)}>{categorie.nom}</li>
            )
       })}
       </div>
        <Data setSelectedCategory={setSelectedCategory} />
        </CategoryContext.Provider>
    </>
)

}
