import {useState, useEffect, useContext} from 'react'
import axios from "axios"
import Produit from "./Produit"
import "bootstrap/dist/css/bootstrap.min.css"
import { CategoryContext } from "./Categorie";

export default function Data(props){

    const [data, setData] = useState([])


    const { selectedCategoryData, setSelectedCategoryData } = useContext(CategoryContext);



    const {selectedCategory} = props;





    useEffect(()=>{
        setSelectedCategoryData(selectedCategory)
    }, [selectedCategory])


    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
        }

        fetchdata()
    }, [])


    return(
        <>
            <div className="container-fluid">
                <div className="row gy-3">
                    {data.filter(item => !selectedCategory || item.categorie === selectedCategory)
                        .map(item =>{
                        return <Produit key={item.id} {...item} item={item}/>
                    })}
                </div>
            </div>

        </>
    )

}
