import "bootstrap/dist/css/bootstrap.min.css"


export default function Panier(){

    const {
        isEmpty, 
        totalUniqueItems,
        items,
        cartTotal,
        updateItemQuantity,
        removeItem,
        emptyCart

    } = useCart()

    if(isEmpty) return <div>votre panier est vide</div>
    return(
        <>
            <h4>panier:({totalUniqueItems})</h4>
            {items.map((item,index)=>{
                return <tr key={index}>
                    <td>
                        <img src={item.image} alt={item.name} style={{height: '3rem'}}/>
                    </td>
                    <td className="ms-4">{item.name}</td>
                    <td>{item.price}</td>
                    <td>Quantit√© ({item.quantity})</td>
                </tr>
            })}

        </>

    )
}
