import {useState} from "react"

import axios from "axios"

import {useCart} from "react-use-cart"

export default function Order(){

    //state (état ou données)

    const [nom, setNom] = useState("")
    const [phone, setPhone] = useState(null)

    const {items} = useCart()

    //comportements

    const handleSubmit = (e)=>{
        e.preventDefault()
        axios({
          method: 'post',
          url: 'http://127.0.0.1:8000/api/cd/orders/',
          data: {
            "nom": nom,
            "phone": phone
          }
        });

        for(let i=0 ; i<items.length; i++){
            //alert(JSON.stringify(items[i].name))
            axios({
              method: 'post',
              url: 'http://127.0.0.1:8000/api/cd/items/',
              data: {
                "quantity": JSON.stringify(items[i].quantity),
                "order": "2",
                "produit": JSON.stringify(items[i].id)
              },
            })
        }
    }

    const handleChange = (e)=>{
        setNom(e.target.value)
    }

    const handleOnChange = (e)=>{
        setPhone(e.target.value)
    }


    //affichage
    return(
        <form onSubmit={handleSubmit}>
            <input value={nom} type="text" placeholder="entrer votre nom ..." onChange={handleChange}/>
            <input value={phone} type="text" placeholder="entrer votre numero ..." onChange={handleOnChange}/>
            <br/>
            <button type="submit">valider</button>
        </form>
    )
}
