import {useState, useEffect} from 'react'
import axios from "axios"
import "bootstrap/dist/css/bootstrap.min.css"

import CategoryContext from "./CategoryContext";


export default function Categorie({children}){


    const [categories, setCategories] = useState([])


    const [selectedCategory, setSelectedCategory] = useState(null);



    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/categories/')
            setCategories(result.data)
        }

        fetchdata()
    }, [])



    const handleCategorySelection = (nom)=>{
        setSelectedCategory(nom)
        //alert(nom)
    }



    return(
    <>

        /*<div>
       {categories.map(categorie=>{
           return(
               <li onClick={()=>handleCategorySelection(categorie.nom)}>{categorie.nom}</li>
            )
       })}
       </div>*/

       <CategoryContext.Provider value={{ selectedCategory, setSelectedCategory,categories, setCategories }}>
            <div>
                <p>{children}</p>
                {categories.map(categorie => {
                    return (
                        <li onClick={() => handleCategorySelection(categorie.nom)}>
                            {categorie.nom}
                        </li>
                    );
                })}
            </div>
        </CategoryContext.Provider>
    </>
)
}
