import {useState, useEffect, useContext} from 'react'
import axios from "axios"
import "bootstrap/dist/css/bootstrap.min.css"
import {CategoryContext} from "./CategoryContext"




export default function Categorie(){


    const [categories, setCategories] = useState([])


    //const [selectedCategory, setSelectedCategory] = useState(null);
    const { selectedCategory, setSelectedCategory } = useContext(CategoryContext);



    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/categories/')
            setCategories(result.data)
        }

        fetchdata()
    }, [])



    const handleCategorySelection = (nom)=>{
        setSelectedCategory(nom)

    }





    return(
    <>
        <li onClick={() => handleCategorySelection(null)}>tous</li>


        <div>
       {categories.map(categorie=>{
           return(
               <span onClick={()=>handleCategorySelection(categorie)} className="me-3">{categorie.nom}</span>
            )
       })}
       </div>

    </>
)
}
