  add: (newModal: Ref<Element>) => {
    modals.push(newModal);
  },

  /**
   * Remove a modal
   */
  remove: (oldModal: Ref<Element>) => {
    modals = modals.filter((modal) => modal !== oldModal);
  },

  /**
   * When multiple modals are rendered will return true if current modal is the last one
   */
