import {useState, useEffect, useContext} from 'react'
import axios from "axios"
import Produit from "./Produit"
import "bootstrap/dist/css/bootstrap.min.css"

import {CategoryContext} from "./CategoryContext"

//message
import Order from "./Order";
import SuccessMessage from "./SuccessMessage"




export default function Data(){

    const [data, setData] = useState([])

    const [searchQuery, setSearchQuery] = useState("");


    const { selectedCategory, setSelectedCategory } = useContext(CategoryContext);

    //debut msg
    const [showOrder, setShowOrder] = useState(false);
  const [showMessage, setShowMessage] = useState(false);

  const handleOrder = () => {
    setShowOrder(true);
  };

  const handleOrderClose = () => {
    setShowOrder(false);
    setShowMessage(true);
  };

    //fin 


    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
        }

        fetchdata()
    }, [])

    return(
        <>
            <div>
      {showOrder ? (
                    <Order handleClose={handleOrderClose} />
                  ) : (
                    <button onClick={handleOrder}>Passer une commande</button>
                  )}

                  {showMessage && <SuccessMessage />}
                </div>
              );
            }



           <div className="container-fluid">
            <div className="row gy-3">
                <div className="px-3 d-flex  pb-2 justify-content-center">
                    <input type="search" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} className="form-control d-md-none rounded-pill px-3" style={{width:"90%", height:"30px"}} placeholder="Rechercher ..."/>
                </div>


              {data.filter(item => !selectedCategory || item.categorie.id === selectedCategory.id).filter(item => item.name.toLowerCase().includes(searchQuery.toLowerCase())).map(item => {
      return <Produit key={item.id} {...item} item={item} />;
    })}

            </div>
          </div>
        </>
    )
}
