import {useState, useEffect} from 'react'
import axios from "axios"
import Produit from "./Produit"
import "bootstrap/dist/css/bootstrap.min.css"
import { useContext } from "react";

import {Select} from "./categorie"



export default function Data(){

    const [data, setData] = useState([])


    const {selectedCategory, setSelectedCategory} = useContext(Select);



    useEffect(()=>{
        const fetchdata = async()=>{
            const result = await axios('http://127.0.0.1:8000/api/produits/')
            setData(result.data)
        }

        fetchdata()
    }, [])






    return(
        <>
            <div className="container-fluid">
                <div className="row gy-3">
                    {data.filter(item => !selectedCategory || item.categorie.id === selectedCategory.id).map(item => {
  return <Produit key={item.id} {...item} item={item} />;
})}
                </div>
            </div>

        </>
    )
}
