import {useCart} from "react-use-cart"

import "bootstrap/dist/css/bootstrap.min.css"
import axios from "axios"



export default function Panier(){

    const {
        isEmpty, 
        totalUniqueItems,
        items,
        cartTotal,
        updateItemQuantity,
        removeItem,
        emptyCart,
        metadata,

    } = useCart()

    let liste = new FormData(); 

    const handleClick = ()=>{
        for(let x of items){
            liste.append("quantity", x.quantity);
        }

        alert(liste)

        axios({
          method: 'post',
          url: 'http://127.0.0.1:8000/api/cd/items/',
          data: {
            liste
          }
        });
    }





    if(isEmpty) return <div>votre panier est vide</div>
    return(
        <div>
            <h4>panier:({totalUniqueItems})</h4>
            {items.map((item,index)=>{
                return <tr key={index}>
                    <td>
                        <img src={item.image} alt={item.name} style={{height: '3rem'}}/>
                    </td>
                    <td>{item.name}</td>
                    <td>{item.price}</td>
                    <td>Quantit√© ({item.quantity})</td>
                </tr>
            })}
            <button className="btn btn-primary" onClick={handleClick}>
                commander
            </button>

        </div>



    )
}
